fun getCountryList() : ArrayList<CountryModel> {
    val json = JSONObject(COUNTRY_LIST)
    val jsonCountries = json.getJSONArray("countries")

    val listCountries = ArrayList<CountryModel>()
    for (index in 0 until jsonCountries.length()) {
        val country     = jsonCountries.getJSONObject(index)
        val code        = country.optString("code")
        val dialCode    = country.optString("dial_code")
        val flag        = country.getString("flag")
        val name        = country.optString("name")
        listCountries.add(CountryModel(code, dialCode, flag, name))
    }

    return listCountries
}